<body>
<header>
<div class="row " >
    <div class="content col-lg-12">
        <div class="col-lg-4" align="right" style="font-size: 14px!important;">
            <p>Fecha: {{date('d')}} de {{$mes}} del {{date('Y')}}</p>


        </div>

    </div>
    <div class="content col-lg-12">
        <div align="center">
            <h2 style="font-size: 16px;padding:10px 0">Reporte de Solicitudes desde {{$fechaDesde}} hasta {{$fechaHasta}}</h2>
        </div>
    </div>

</div>
</header>

<footer class="page derecha"></footer>

<table  class="pure-table table">
    <thead class="thead-dark">
    <tr>
        <th scope="col">#</th>
        <th scope="col">Tipo</th>
        <th scope="col">Descripcion</th>
        <th scope="col">Status</th>
        <th scope="col">Usuario Solicitud</th>
        <th scope="col">Fecha Solicitud</th>
    </tr>
    </thead>
    <tbody>
    @foreach($solicitudes as $solicitud)
            <tr>
                <td>00{{$solicitud->id}}</td>
                <td>{{ucwords($solicitud->titulo)}}</td>
                <td>{{$solicitud->descripcion}}</td>
                @if($solicitud->status==='enviada')
                    <td class="text-center label label-light-info">{{strtoupper( $solicitud->status )}}</td>
                @else
                    @if($solicitud->status==='aceptada')
                        <td class="text-center label label-light-success">{{strtoupper( $solicitud->status )}}</td>
                    @else
                        <td class="text-center label label-light-danger">{{strtoupper( $solicitud->status )}}</td>
                    @endif
                @endif
                <td class="text-center">{{ $solicitud->user->name.' '.$solicitud->user->last_name}}<br/> Rol: {{ucwords($solicitud->user->rol)}} <br/>   Cedula: {{$solicitud->user->cedula}} <strong>({{ $solicitud->user->email}})</strong></td>

